import { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import { Link, useNavigate } from 'react-router-dom';
import toast from 'react-hot-toast';

const BuyerHeader = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [showBalanceDropdown, setShowBalanceDropdown] = useState(false);
  const dropdownRef = useRef(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowBalanceDropdown(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleLogout = async () => {
    try {
      await logout();
      toast.success('Logged out successfully');
      navigate('/login');
    } catch (error) {
      toast.error('Failed to logout');
    }
  };

  const handleAddCoins = () => {
    toast.success('Opening payment gateway...');
    // Navigate to payments page
    navigate('/buyer/payments');
  };

  return (
    <header className="bg-white shadow-sm border-b sticky top-0 z-10">
      <div className="px-6 py-4 flex justify-between items-center">
        <div className="flex items-center space-x-4">
          {/* Back Button */}
          <button
            onClick={() => navigate('/')}
            className="flex items-center space-x-2 text-gray-600 hover:text-gray-900 transition-colors duration-200"
          >
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path d="M10 19l-7-7m0 0l7-7m-7 7h18" strokeWidth={2} />
            </svg>
            <span className="text-sm font-medium">Home</span>
          </button>

          {/* Logo */}
          <div className="flex items-center space-x-2">
            <div className="w-8 h-8 bg-gradient-to-r from-blue-600 to-indigo-600 rounded-lg flex items-center justify-center">
              <svg className="w-5 h-5 text-white" fill="currentColor" viewBox="0 0 20 20">
                <path d="M10.394 2.08a1 1 0 00-.788 0l-7 3a1 1 0 000 1.84L5.25 8.051a.999.999 0 01.356-.257l4-1.714a1 1 0 11.788 1.838L7.667 9.088l1.94.831a1 1 0 00.787 0l7-3a1 1 0 000-1.838l-7-3zM3.31 9.397L5 10.12v4.102a8.969 8.969 0 00-1.05-.174 1 1 0 01-.89-.89 11.115 11.115 0 01.25-3.762zM9.3 16.573A9.026 9.026 0 007 14.935v-3.957l1.818.78a3 3 0 002.364 0l5.508-2.361a11.026 11.026 0 01.25 3.762 1 1 0 01-.89.89 8.968 8.968 0 00-5.35 2.524 1 1 0 01-1.4 0zM6 18a1 1 0 001-1v-2.065a8.935 8.935 0 00-2-.712V17a1 1 0 001 1z" />
              </svg>
            </div>
            <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
              Buyer Panel
            </h1>
          </div>
        </div>

        <div className="flex items-center space-x-6">
          {/* Balance Dropdown */}
          <div className="relative" ref={dropdownRef}>
            <button
              onClick={() => setShowBalanceDropdown(!showBalanceDropdown)}
              className="flex items-center bg-gradient-to-r from-blue-50 to-indigo-50 px-4 py-2 rounded-full border border-blue-100 hover:shadow-md transition-all duration-200 cursor-pointer"
            >
              <span className="text-xs font-bold text-blue-600 uppercase tracking-tighter mr-2">Balance</span>
              <span className="font-bold text-blue-700">{user?.coins || user?.balance || 0} Coins</span>
              <svg className="w-4 h-4 ml-2 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path d="M19 9l-7 7-7-7" strokeWidth={2} />
              </svg>
            </button>

            {/* Dropdown Menu */}
            {showBalanceDropdown && (
              <div className="absolute right-0 mt-2 w-80 bg-white rounded-2xl shadow-2xl border border-gray-100 overflow-hidden z-50">
                {/* Header */}
                <div className="bg-gradient-to-r from-blue-600 to-indigo-600 p-4 text-white">
                  <div className="flex justify-between items-start">
                    <div>
                      <p className="text-sm font-medium opacity-90">Current Balance</p>
                      <p className="text-3xl font-bold">{user?.coins || user?.balance || 0}</p>
                      <p className="text-xs opacity-75 mt-1">Available for purchases</p>
                    </div>
                    <div className="bg-white/20 rounded-full p-2">
                      <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                        <path d="M4 4v12h12V4H4zm2 4h8v4H6V8zm2 4h8v4H8v-4zm2-4v4h2V4h-2z" />
                      </svg>
                    </div>
                  </div>
                </div>

                {/* Quick Stats */}
                <div className="p-4 space-y-3">
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Tasks Posted</span>
                    <span className="text-sm font-semibold text-blue-600">12 tasks</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Total Spent</span>
                    <span className="text-sm font-semibold text-indigo-600">$2,450</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Available</span>
                    <span className="text-sm font-semibold text-green-600">{user?.coins || user?.balance || 0} coins</span>
                  </div>
                </div>

                {/* Actions */}
                <div className="p-4 border-t border-gray-100 space-y-2">
                  <button
                    onClick={handleAddCoins}
                    className="w-full flex items-center justify-center space-x-2 bg-gradient-to-r from-blue-600 to-indigo-600 text-white px-4 py-2 rounded-xl font-medium hover:from-blue-700 hover:to-indigo-700 transition-all duration-200"
                  >
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path d="M12 4v16m8-8H4" strokeWidth={2} />
                    </svg>
                    <span>Add Coins</span>
                  </button>
                  <Link
                    to="/buyer/create-task"
                    className="block w-full flex items-center justify-center space-x-2 bg-gray-100 text-gray-700 px-4 py-2 rounded-xl font-medium hover:bg-gray-200 transition-colors duration-200"
                  >
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path d="M12 4v16m8-8H4" strokeWidth={2} />
                    </svg>
                    <span>Create Task</span>
                  </Link>
                </div>
              </div>
            )}
          </div>

          <div className="flex items-center space-x-3 border-l pl-6 border-gray-100">
            <button
              onClick={handleLogout}
              className="text-sm font-semibold text-gray-500 hover:text-red-600 transition-colors"
            >
              Logout
            </button>
            <div className="h-8 w-8 rounded-full bg-gray-200 overflow-hidden border border-gray-100">
              <img
                src={user?.photoURL || `https://ui-avatars.com/api/?name=${encodeURIComponent(user?.displayName || 'U')}`}
                alt="Profile"
                className="h-full w-full object-cover"
              />
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};

export default BuyerHeader;
