import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { isFirebaseConfigured } from '../../services/firebase.config';
import toast from 'react-hot-toast';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const { login, loginWithGoogle, loginWithFacebook, loginWithGithub, user } = useAuth();
  const navigate = useNavigate();

  // Redirect if already logged in
  useEffect(() => {
    if (user && !loading) {
      if (user.role === 'worker') navigate('/worker/dashboard');
      else if (user.role === 'buyer') navigate('/buyer/dashboard');
      else if (user.role === 'admin') navigate('/admin/dashboard');
      else navigate('/select-role');
    }
  }, [user, loading, navigate]);

  const handleLoginResult = (result, provider) => {
    if (result.success) {
      toast.success('Login successful!');
      if (!result.user.initialCoinsReceived && !result.user.role) {
        navigate('/select-role');
      } else {
        if (result.user.role === 'worker') navigate('/worker/dashboard');
        else if (result.user.role === 'buyer') navigate('/buyer/dashboard');
        else if (result.user.role === 'admin') navigate('/admin/dashboard');
        else navigate('/select-role');
      }
    } else {
      toast.error(result.error || `${provider} login failed`);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Form validation
    if (!email.trim()) {
      toast.error('Please enter your email address');
      return;
    }
    
    if (!password.trim()) {
      toast.error('Please enter your password');
      return;
    }
    
    if (password.length < 6) {
      toast.error('Password must be at least 6 characters');
      return;
    }
    
    // Email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      toast.error('Please enter a valid email address');
      return;
    }

    setLoading(true);
    const result = await login(email, password);
    handleLoginResult(result, 'Email');
    setLoading(false);
  };

  const handleSocialLogin = async (provider) => {
    setLoading(true);
    let result;
    if (provider === 'Google') result = await loginWithGoogle();
    else if (provider === 'Facebook') result = await loginWithFacebook();
    else if (provider === 'GitHub') result = await loginWithGithub();

    handleLoginResult(result, provider);
    setLoading(false);
  };

  return (
    <div className="min-h-screen grid grid-cols-1 lg:grid-cols-2 bg-white overflow-hidden font-sansSelection">
      {/* Left side: Premium Illustration & Content */}
      <div className="hidden lg:flex flex-col justify-center items-center bg-gray-50 relative p-20 overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-br from-indigo-600/5 to-blue-600/5 z-0" />
        <div className="relative z-10 space-y-8 max-w-lg text-center">
          <div className="inline-flex items-center space-x-2 px-4 py-2 bg-indigo-50 text-indigo-700 rounded-full text-xs font-black uppercase tracking-widest animate-pulse">
            <span className="w-2 h-2 bg-indigo-600 rounded-full"></span>
            <span>Active Ecosystem</span>
          </div>
          <h1 className="text-6xl font-black text-gray-900 leading-none tracking-tighter uppercase">
            Unlock Your <br />
            <span className="text-indigo-600">Digital Potential</span>
          </h1>
          <p className="text-gray-500 font-medium text-lg italic">
            Join thousands of creators and earners in the world's most advanced micro-task protocol.
          </p>
          <div className="relative group">
            <div className="absolute -inset-4 bg-gradient-to-r from-indigo-500 to-blue-500 rounded-[3rem] blur opacity-25 group-hover:opacity-40 transition duration-1000 group-hover:duration-200"></div>
            <img
              src="https://img.freepik.com/free-vector/digital-lifestyle-concept-illustration_114360-7327.jpg"
              alt="Digital Growth"
              className="relative rounded-[2.5rem] shadow-2xl border border-white/50 w-full object-cover aspect-video bg-white"
            />
          </div>
        </div>

        {/* Floating Decorative Elements */}
        <div className="absolute top-20 left-20 w-32 h-32 bg-blue-400/10 rounded-full blur-3xl" />
        <div className="absolute bottom-20 right-20 w-48 h-48 bg-indigo-400/10 rounded-full blur-3xl" />
      </div>

      {/* Right side: Login Form */}
      <div className="flex flex-col justify-center items-center p-8 lg:p-20 bg-white relative">
        <div className="w-full max-w-md space-y-12 animate-in fade-in slide-in-from-right-8 duration-700">
          <div className="space-y-4">
            <Link to="/" className="inline-flex items-center text-indigo-600 font-black text-xs uppercase tracking-widest hover:translate-x-[-4px] transition-transform">
              <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path d="M10 19l-7-7m0 0l7-7m-7 7h18" strokeWidth={2.5} /></svg>
              Back to Core
            </Link>
            <h2 className="text-4xl font-black text-gray-900 uppercase tracking-tight">Identity Verification</h2>
            <p className="text-gray-400 font-medium">Access your personalized dashboard and treasury.</p>
          </div>

          {!isFirebaseConfigured && (
            <div className="bg-amber-50 border-l-4 border-amber-400 p-4 rounded-2xl">
              <div className="flex items-center">
                <svg className="w-5 h-5 text-amber-400 mr-3" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" /></svg>
                <p className="text-amber-800 text-xs font-bold uppercase tracking-wider">Protocol Initialization Required: Check Environment Variables</p>
              </div>
            </div>
          )}

          <form className="space-y-8" onSubmit={handleSubmit}>
            <div className="space-y-6">
              <div className="space-y-2">
                <label className="text-[10px] font-black text-gray-400 uppercase tracking-[0.2em] ml-2">Email Address</label>
                <div className="relative group">
                  <input
                    type="email"
                    required
                    className="w-full px-6 py-5 rounded-3xl bg-gray-50 border-2 border-transparent focus:bg-white focus:border-indigo-600 focus:ring-4 focus:ring-indigo-600/5 transition-all outline-none font-bold text-gray-900 placeholder:text-gray-400"
                    placeholder="Enter email address"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                  <div className="absolute right-6 top-1/2 -translate-y-1/2 text-gray-300 group-focus-within:text-indigo-600 transition-colors">
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path d="M16 12a4 4 0 10-8 0 4 4 0 008 0zm0 0v1.5a2.5 2.5 0 005 0V12a9 9 0 10-9 9m4.5-1.206a8.959 8.959 0 01-4.5 1.206" strokeWidth={2.5} /></svg>
                  </div>
                </div>
              </div>

              <div className="space-y-2">
                <div className="flex justify-between items-center ml-2">
                  <label className="text-[10px] font-black text-gray-400 uppercase tracking-[0.2em] ml-2">Enter Your Password</label>
                  <button type="button" className="text-[10px] font-black text-indigo-600 uppercase tracking-widest hover:text-indigo-700 transition-colors">Forgot Password?</button>
                </div>
                <div className="relative group">
                  <input
                    type={showPassword ? "text" : "password"}
                    required
                    className="w-full px-6 py-5 rounded-3xl bg-gray-50 border-2 border-transparent focus:bg-white focus:border-indigo-600 focus:ring-4 focus:ring-indigo-600/5 transition-all outline-none font-bold text-gray-900 placeholder:text-gray-400"
                    placeholder="••••••••"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-6 top-1/2 -translate-y-1/2 text-gray-300 group-focus-within:text-indigo-600 transition-colors hover:text-indigo-600"
                  >
                    {showPassword ? (
                      <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.88 9.88l-3.29-3.29m7.532 7.532l3.29 3.29M3 3l3.59 3.59m0 0A9.953 9.953 0 0112 5c4.478 0 8.268 2.943 9.543 7a10.025 10.025 0 01-4.132 5.411m0 0L21 21" strokeWidth={2.5} />
                      </svg>
                    ) : (
                      <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" strokeWidth={2.5} />
                        <path d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" strokeWidth={2.5} />
                      </svg>
                    )}
                  </button>
                </div>
              </div>
            </div>

            <button
              type="submit"
              disabled={loading}
              className="w-full bg-gradient-to-r from-indigo-600 via-purple-600 to-indigo-700 text-white font-black py-6 rounded-3xl text-sm uppercase tracking-[0.2em] shadow-2xl shadow-indigo-500/30 hover:-translate-y-1 active:translate-y-0 transition-all disabled:opacity-50 flex items-center justify-center space-x-2"
            >
              {loading ? (
                <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
              ) : (
                <>
                  <span>Initialize Session</span>
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path d="M13 7l5 5m0 0l-5 5m5-5H6" strokeWidth={2.5} /></svg>
                </>
              )}
            </button>
          </form>

          <div className="space-y-8">
            <div className="relative">
              <div className="absolute inset-0 flex items-center">
                <div className="w-full border-t border-gray-100"></div>
              </div>
              <div className="relative flex justify-center text-xs">
                <span className="px-6 bg-white text-gray-400 font-extrabold uppercase tracking-widest">Multi-Channel Access</span>
              </div>
            </div>

            <div className="grid grid-cols-3 gap-4">
              <button
                type="button"
                onClick={() => handleSocialLogin('Google')}
                disabled={loading}
                className="flex items-center justify-center py-4 px-4 border-2 border-gray-50 rounded-2xl hover:bg-gray-50 hover:border-gray-100 transition-all disabled:opacity-50 group"
              >
                <svg className="h-6 w-6" viewBox="0 0 24 24">
                  <path fill="currentColor" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z" />
                  <path fill="currentColor" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z" />
                  <path fill="currentColor" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z" />
                  <path fill="currentColor" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z" />
                </svg>
              </button>

              <button
                type="button"
                onClick={() => handleSocialLogin('Facebook')}
                disabled={loading}
                className="flex items-center justify-center py-4 px-4 border-2 border-gray-50 rounded-2xl hover:bg-blue-50/50 hover:border-blue-100 transition-all disabled:opacity-50 text-[#1877f2]"
              >
                <svg className="h-6 w-6" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z" />
                </svg>
              </button>

              <button
                type="button"
                onClick={() => handleSocialLogin('GitHub')}
                disabled={loading}
                className="flex items-center justify-center py-4 px-4 border-2 border-gray-50 rounded-2xl hover:bg-gray-900/5 hover:border-gray-200 transition-all disabled:opacity-50 text-gray-900"
              >
                <svg className="h-6 w-6" fill="currentColor" viewBox="0 0 24 24">
                  <path fillRule="evenodd" d="M12 2C6.477 2 2 6.484 2 12.017c0 4.425 2.865 8.18 6.839 9.504.5.092.682-.217.682-.483 0-.237-.008-.868-.013-1.703-2.782.605-3.369-1.343-3.369-1.343-.454-1.158-1.11-1.466-1.11-1.466-.908-.62.069-.608.069-.608 1.003.07 1.531 1.032 1.531 1.032.892 1.53 2.341 1.088 2.91.832.092-.647.35-1.088.636-1.338-2.22-.253-4.555-1.113-4.555-4.951 0-1.093.39-1.988 1.029-2.688-.103-.253-.446-1.272.098-2.65 0 0 .84-.27 2.75 1.026A9.564 9.564 0 0112 6.844c.85.004 1.705.115 2.504.337 1.909-1.296 2.747-1.027 2.747-1.027.546 1.379.202 2.398.1 2.651.64.7 1.028 1.595 1.028 2.688 0 3.848-2.339 4.695-4.566 4.943.359.309.678.92.678 1.855 0 1.338-.012 2.419-.012 2.747 0 .268.18.58.688.482A10.019 10.019 0 0022 12.017C22 6.484 17.522 2 12 2z" clipRule="evenodd" />
                </svg>
              </button>
            </div>
          </div>

          <div className="pt-10 flex justify-center">
            <p className="text-sm font-bold text-gray-400">
              New to the protocol?
              <Link to="/register" className="ml-2 text-indigo-600 font-extrabold uppercase tracking-widest hover:text-indigo-700 transition-colors">
                Establish Identity
              </Link>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
